import React, { useState } from 'react';

const AttendanceCheck = () => {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [attendance, setAttendance] = useState(null);

  const openCamera = () => {
    // Giả lập nhận diện khuôn mặt
    setAttendance({
      id: '22BI13029',
      name: 'Nguyễn Thị Hà Anh',
      time: new Date().toLocaleString('vi-VN'),
    });
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Attendance check</h2>
      <div className="flex gap-4 mb-6">
        <select
          value={selectedSubject}
          onChange={(e) => setSelectedSubject(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="">Choose subject</option>
          <option value="OOP">Object-Oriented Programming</option>
          <option value="ADS">Algorithms and Data Structures</option>
          <option value="DSP">Digital Signal Processing</option>
        </select>
        <button
          onClick={openCamera}
          className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Open camera
        </button>
      </div>
      {attendance && (
        <div className="bg-gray-100 p-4 rounded">
          <p><strong>Student ID:</strong> {attendance.id}</p>
          <p><strong>Full name:</strong> {attendance.name}</p>
          <p><strong>Attendance time:</strong> {attendance.time}</p>
        </div>
      )}
    </div>
  );
};

export default AttendanceCheck;
