import React, { useState, useEffect, useRef } from 'react';

const AttendanceCheck = () => {
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [faceId, setFaceId] = useState('1'); // ID người dùng
  const [attendanceInfo, setAttendanceInfo] = useState({
    studentId: '',
    fullName: '',
    attendanceTime: '',
    imageUrl: 'https://via.placeholder.com/150',
  });

  // Dữ liệu giả lập từ Quản lý lịch học
  const lectureSchedule = [
    { subject: 'Object-Oriented Programming', lecture: 'Lecture 1', time: '08:00 - 09:30' },
    { subject: 'Algorithms and Data Structures', lecture: 'Lecture 2', time: '10:00 - 11:30' },
    { subject: 'Digital Signal Procssing', lecture: 'Lecture 1', time: '08:00 - 09:30' },
  ];

  const [lectureInfo, setLectureInfo] = useState({
    subject: '',
    lecture: '',
    time: '',
  });

  // Cập nhật thông tin tiết học khi chọn môn
  useEffect(() => {
    const selectedLecture = lectureSchedule.find(item => item.subject === selectedSubject);
    if (selectedLecture) {
      setLectureInfo(selectedLecture);
    } else {
      setLectureInfo({ subject: '', lecture: '', time: '' });
    }
  }, [selectedSubject]);

  // Mở camera và bắt đầu capture
  const openCamera = async () => {
    try {
      console.log("[INFO] Gọi API start_capture...");
      const response = await fetch('http://localhost:5000/start_capture', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ face_id: faceId }),
      });
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP error! Status: ${response.status}, Message: ${errorText}`);
      }
      const data = await response.json();
      console.log("[INFO] Phản hồi từ backend:", data);
      if (data.status === 'started') {
        setIsCameraOpen(true);
        if (videoRef.current) {
          videoRef.current.src = 'http://localhost:5000/video_feed'; // Gán nguồn video feed
          videoRef.current.onerror = () => {
            console.error("[ERROR] Không thể tải video feed.");
            alert("Không thể tải video feed. Vui lòng kiểm tra backend.");
          };
          videoRef.current.onload = () => {
            console.log("[INFO] Video feed đã tải.");
          };
        }
      } else {
        throw new Error(`Trạng thái không phải "started". Nhận được: ${JSON.stringify(data)}`);
      }
    } catch (error) {
      console.error('Lỗi khi mở camera:', error);
      alert(`Không thể mở camera. Chi tiết: ${error.message}`);
    }
  };

  // Đóng camera
  const closeCamera = async () => {
    try {
      const response = await fetch('http://localhost:5000/stop_capture', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      if (data.status === 'stopped') {
        setIsCameraOpen(false);
        if (videoRef.current) {
          videoRef.current.src = ''; // Xóa nguồn khi đóng
        }
      }
    } catch (error) {
      console.error('Lỗi khi đóng camera:', error);
    }
  };

  // Cập nhật thông tin điểm danh
  const handleCheckAttendance = () => {
    setAttendanceInfo(prev => ({
      ...prev,
      studentId: 'ST001',
      fullName: 'Nguyễn Văn A',
      attendanceTime: new Date().toLocaleTimeString(),
    }));
  };

  return (
    <div className="p-2 h-full overflow-hidden">
      <h2 className="text-2xl font-bold mb-2 text-gray-800">Điểm danh</h2>
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 h-[calc(100%-2rem)]">
        {/* Ô lớn - Webcam */}
        <div className="lg:col-span-2 bg-gray-200 rounded-lg p-4 flex flex-col items-center justify-center h-full">
          <div className="relative w-full mb-4">
            <button
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="w-full p-2 border rounded text-left bg-white"
            >
              {selectedSubject || 'Choose subject'}
            </button>
            {isDropdownOpen && (
              <div className="absolute w-full mt-1 bg-white border rounded shadow-md z-10">
                <button
                  onClick={() => {
                    setSelectedSubject('Môn học 1');
                    setIsDropdownOpen(false);
                  }}
                  className="w-full text-left p-2 hover:bg-gray-100"
                >
                  Môn học 1
                </button>
                <button
                  onClick={() => {
                    setSelectedSubject('Môn học 2');
                    setIsDropdownOpen(false);
                  }}
                  className="w-full text-left p-2 hover:bg-gray-100"
                >
                  Môn học 2
                </button>
              </div>
            )}
          </div>
          <div className="flex-1 flex items-center justify-center">
            {isCameraOpen ? (
              <img
                ref={videoRef}
                alt="Webcam Feed with Face Detection"
                className="w-full h-full object-contain rounded-lg"
                style={{ maxHeight: 'calc(100% - 60px)' }} // Trừ khoảng cách cho nút
              />
            ) : (
              <div className="text-gray-500">Camera chưa được mở</div>
            )}
          </div>
          <div className="flex gap-2 mt-4 justify-center">
            {!isCameraOpen ? (
              <button
                onClick={openCamera}
                className="bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
              >
                Mở camera
              </button>
            ) : (
              <button
                onClick={closeCamera}
                className="bg-red-600 text-white p-2 rounded hover:bg-red-700"
              >
                Đóng camera
              </button>
            )}
            <button
              onClick={handleCheckAttendance}
              className="bg-green-600 text-white p-2 rounded hover:bg-green-700"
            >
              Kiểm tra điểm danh
            </button>
          </div>
        </div>

        {/* Cột chứa 2 ô nhỏ */}
        <div className="lg:col-span-1 flex flex-col gap-4 h-full">
          {/* Ô thông tin điểm danh */}
          <div className="bg-white p-4 rounded-lg shadow-md flex-3 overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Thông tin điểm danh</h3>
            <div className="flex flex-col items-center">
              <img
                src={attendanceInfo.imageUrl}
                alt="Student"
                className="w-40 h-40 object-cover rounded-full mb-4 border-2 border-gray-300"
              />
              <p className="text-gray-600 my-4 text-left w-full">Student ID: {attendanceInfo.studentId}</p>
              <p className="text-gray-600 my-4 text-left w-full">Full name: {attendanceInfo.fullName}</p>
              <p className="text-gray-600 my-4 text-left w-full">Attendance time: {attendanceInfo.attendanceTime}</p>
            </div>
          </div>

          {/* Ô thông tin tiết học */}
          <div className="bg-white p-4 rounded-lg shadow-md flex-0.5 overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Thông tin tiết học</h3>
            <p className="text-gray-600 my-4">Subject: {lectureInfo.subject}</p>
            <p className="text-gray-600 my-4">Lecture: {lectureInfo.lecture}</p>
            <p className="text-gray-600 my-4">Time: {lectureInfo.time}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceCheck;
