import React, { useState, useEffect, useRef } from 'react';

const AttendanceCheck = () => {
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [attendanceInfo, setAttendanceInfo] = useState({
    studentId: '',
    fullName: '',
    attendanceTime: '',
    imageUrl: 'https://via.placeholder.com/150', // Tăng kích thước ảnh placeholder
  });
  const [lectureInfo, setLectureInfo] = useState({
    subject: 'Toán',
    lecture: 'Tiết 1',
    time: '08:00 - 09:30',
  });

  // Mở camera
  const openCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsCameraOpen(true);
      }
    } catch (error) {
      console.error('Lỗi khi mở camera:', error);
      alert('Không thể mở camera. Vui lòng kiểm tra quyền truy cập hoặc thiết bị.');
    }
  };

  // Đóng camera
  const closeCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const stream = videoRef.current.srcObject;
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setIsCameraOpen(false);
  };

  // Giả lập cập nhật thông tin điểm danh
  const handleCheckAttendance = () => {
    setAttendanceInfo({
      studentId: 'ST001',
      fullName: 'Nguyễn Văn A',
      attendanceTime: new Date().toLocaleTimeString(),
      imageUrl: 'https://via.placeholder.com/150', // Thay bằng ảnh từ backend
    });
  };

  useEffect(() => {
    return () => {
      closeCamera(); // Đóng camera khi component unmount
    };
  }, []);

  return (
    <div className="p-2 h-full overflow-hidden">
      <h2 className="text-2xl font-bold mb-2 text-gray-800">Điểm danh</h2>
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 h-[calc(100%-2rem)]">
        {/* Ô lớn - Webcam */}
        <div className="lg:col-span-2 bg-gray-200 rounded-lg p-4 flex flex-col items-center justify-center h-full">
          <div className="relative w-full mb-4">
            <button
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="w-full p-2 border rounded text-left bg-white"
            >
              {selectedSubject || 'Choose subject'}
            </button>
            {isDropdownOpen && (
              <div className="absolute w-full mt-1 bg-white border rounded shadow-md z-10">
                <button
                  onClick={() => {
                    setSelectedSubject('Môn học 1');
                    setIsDropdownOpen(false);
                  }}
                  className="w-full text-left p-2 hover:bg-gray-100"
                >
                  Môn học 1
                </button>
                <button
                  onClick={() => {
                    setSelectedSubject('Môn học 2');
                    setIsDropdownOpen(false);
                  }}
                  className="w-full text-left p-2 hover:bg-gray-100"
                >
                  Môn học 2
                </button>
              </div>
            )}
          </div>
          {isCameraOpen ? (
            <>
              <video
                ref={videoRef}
                autoPlay
                playsInline
                className="w-full h-[80%] object-cover rounded-lg"
              />
              <button
                onClick={closeCamera}
                className="mt-2 bg-red-600 text-white p-2 rounded hover:bg-red-700"
              >
                Đóng camera
              </button>
            </>
          ) : (
            <>
              <div className="w-full h-[80%] flex items-center justify-center text-gray-500">
                Camera chưa được mở
              </div>
              <button
                onClick={openCamera}
                className="mt-2 bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
              >
                Mở camera
              </button>
            </>
          )}
          <button
            onClick={handleCheckAttendance}
            className="mt-2 bg-green-600 text-white p-2 rounded hover:bg-green-700"
          >
            Kiểm tra điểm danh
          </button>
        </div>

        {/* Cột chứa 2 ô nhỏ */}
        <div className="lg:col-span-1 flex flex-col gap-4 h-full">
          {/* Ô thông tin điểm danh */}
          <div className="bg-white p-4 rounded-lg shadow-md flex-2 overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Thông tin điểm danh</h3>
            <div className="flex flex-col items-start">
              <img
                src={attendanceInfo.imageUrl}
                alt="Student"
                className="w-32 h-32 object-cover rounded-full mb-4 border-2 border-gray-300"
              />
              <p className="text-gray-600 my-4">Student ID: {attendanceInfo.studentId}</p>
              <p className="text-gray-600 my-4">Full name: {attendanceInfo.fullName}</p>
              <p className="text-gray-600 my-4">Attendance time: {attendanceInfo.attendanceTime}</p>
            </div>
          </div>

          {/* Ô thông tin tiết học */}
          <div className="bg-white p-4 rounded-lg shadow-md flex-1 overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Thông tin tiết học</h3>
            <p className="text-gray-600 my-4">Subject: {lectureInfo.subject}</p>
            <p className="text-gray-600 my-4">Lecture: {lectureInfo.lecture}</p>
            <p className="text-gray-600 my-4">Time: {lectureInfo.time}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceCheck;
