import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../api/axios'; 
import { jwtDecode } from 'jwt-decode';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useAuth } from '../context/authContext';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')).access_token : null;
    setError('');
    setLoading(true);

    try {
      const response = await axios.post(
        `${import.meta.env.VITE_API_BASE_URL}/login`,
        { email, password },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      console.log("API Response:", response);
      if (response.status >= 200 && response.status < 300) {
        const responseBody = JSON.parse(response.data.body);
        const { access_token } = responseBody;
        console.log("User Data from API:", access_token);

        if (!access_token) {
          toast.error('Thông tin đăng nhập không hợp lệ');
          setLoading(false);
          return;
        }

        const decodedToken = jwtDecode(access_token);
        console.log("Decoded JWT:", decodedToken);

        if (!decodedToken) {
          setError('Failed to decode token');
          return;
        }

        const user = {
          access_token,
          refresh_token: responseBody.refresh_token,
          user_id: decodedToken.user_id,
          user_name: decodedToken.user_name,
          role: decodedToken.role,
        };

        login(user);
        localStorage.setItem('user', JSON.stringify(user));
        console.log("Login: User after login:", user);

        const role = decodedToken.role;
        console.log("User Role:", role);
        console.log("Role Type:", typeof role);

        if (role) {
          const trimmedRole = role.trim();
          console.log("Trimmed User Role:", trimmedRole);

          if (trimmedRole === 'Student') {
            console.log("Redirecting to Student Dashboard...");
            navigate('/student-dashboard');
          } else if (trimmedRole === 'Teacher') {
            console.log("Redirecting to Teacher Dashboard...");
            navigate('/teacher-dashboard');
          } else {
            console.error("Unknown role:", trimmedRole);
            setError('Unknown role');
          }
        } else {
          console.error("Role is missing or invalid");
          setError('Role is missing or invalid');
        }
      } else {
        console.error("Login failed with status:", response.status);
        setError('Invalid credentials or something went wrong');
        toast.error('Invalid email or password');
      }
    } catch (error) {
      console.error("Login failed:", error);
      setError('Invalid credentials or something went wrong');
      toast.error('An error occurred, please try again');
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = () => {
    navigate('/register');
  };

  return (
    <div className="login-page flex items-center justify-center h-screen bg-gray-100">
      <div className="login-container bg-white p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-bold mb-6 text-center">Welcome Back!</h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="input-group mb-4">
            <label className="block text-gray-700 font-semibold mb-2">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
              className="w-full p-2 border rounded"
            />
          </div>
          <div className="input-group mb-6">
            <label className="block text-gray-700 font-semibold mb-2">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
              className="w-full p-2 border rounded"
            />
          </div>
          {error && <p className="text-red-500 text-center mb-4">{error}</p>}
          <button
            className="login-button w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
            type="submit"
            disabled={loading}
          >
            {loading ? "Logging in..." : "Log in"}
          </button>
        </form>
        <div className="register-section mt-6 text-center">
          <p className="text-gray-600 mb-2">New to the platform?</p>
          <button
            className="register-button bg-gray-200 text-gray-800 p-2 rounded hover:bg-gray-300"
            onClick={handleRegister}
          >
            Create an Account
          </button>
        </div>
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
};

export default Login;
