import React, { useState, useEffect, useRef } from 'react';
import Webcam from 'react-webcam';

const AttendanceCheck = () => {
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [faceId] = useState('1');

  const webcamRef = useRef(null);

  const [attendanceInfo, setAttendanceInfo] = useState({
    studentId: '',
    fullName: '',
    attendanceTime: '',
    imageUrl: 'https://via.placeholder.com/150',
  });

  const videoConstraints = {
    width: 1280,
    height: 720,
    facingMode: "user",
  };

  const lectureSchedule = [
    { subject: 'Object-Oriented Programming', lecture: 'Lecture 1', time: '08:00 - 09:30' },
    { subject: 'Algorithms and Data Structures', lecture: 'Lecture 2', time: '10:00 - 11:30' },
    { subject: 'Digital Signal Processing', lecture: 'Lecture 3', time: '13:00 - 14:30' },
  ];

  const [lectureInfo, setLectureInfo] = useState({
    subject: '',
    lecture: '',
    time: '',
  });

  useEffect(() => {
    const selectedLecture = lectureSchedule.find(item => item.subject === selectedSubject);
    setLectureInfo(selectedLecture || { subject: '', lecture: '', time: '' });
  }, [selectedSubject]);

  const toggleCamera = () => {
    setIsCameraOpen(prev => !prev);
  };

  const handleCheckAttendance = async () => {
    if (!webcamRef.current) {
      alert("Webcam chưa sẵn sàng.");
      return;
    }

    if (!selectedSubject) {
      alert("Vui lòng chọn môn học.");
      return;
    }

    const screenshot = webcamRef.current.getScreenshot();
    if (!screenshot) {
      alert("Không thể chụp ảnh từ webcam.");
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/upload_attendance', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          face_id: faceId,
          subject: selectedSubject,
          image: screenshot,
        }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error('HTTP error ${response.status}: ${errorText}');
      }

      const data = await response.json();
      console.log("[INFO] Kết quả điểm danh:", data);

      setAttendanceInfo({
        studentId: data.studentId || 'N/A',
        fullName: data.fullName || 'Unknown',
        attendanceTime: data.attendanceTime || new Date().toLocaleTimeString(),
        imageUrl: data.imageUrl || screenshot,
      });

    } catch (err) {
      console.error("Lỗi khi gửi ảnh điểm danh:", err);
      alert("Không thể gửi ảnh điểm danh.\nChi tiết: " + err.message);
    }
  };

  return (
    <div className="p-2 h-full overflow-hidden">
      <h2 className="text-2xl font-bold mb-2 text-gray-800">Attendance check</h2>
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 h-[calc(100%-2rem)]">
        {/* Webcam Section */}
        <div className="lg:col-span-2 bg-gray-200 rounded-lg p-4 flex flex-col items-center justify-center h-full">
          {/* Subject Dropdown */}
          <div className="relative w-full mb-4">
            <button
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="w-full p-2 border rounded text-left bg-white"
            >
              {selectedSubject || 'Choose subject'}
            </button>
            {isDropdownOpen && (
              <div className="absolute w-full mt-1 bg-white border rounded shadow-md z-10">
                {lectureSchedule.map(item => (
                  <button
                    key={item.subject}
                    onClick={() => {
                      setSelectedSubject(item.subject);
                      setIsDropdownOpen(false);
                    }}
                    className="w-full text-left p-2 hover:bg-gray-100"
                  >
                    {item.subject}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Webcam */}
          <div className="flex-1 flex items-center justify-center w-full">
            {isCameraOpen ? (
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                videoConstraints={videoConstraints}
                className="rounded-lg border w-full h-full object-contain"
              />
            ) : (
              <div className="text-gray-500">Camera is off</div>
            )}
          </div>

          {/* Buttons */}
          <div className="flex gap-2 mt-4 justify-center">
            <button
              onClick={toggleCamera}
              className={`${
                isCameraOpen ? 'bg-red-600 hover:bg-red-700' : 'bg-blue-600 hover:bg-blue-700'
              } text-white p-2 rounded`}
            >
              {isCameraOpen ? 'Close camera' : 'Open camera'}
            </button>
            <button
              onClick={handleCheckAttendance}
              className="bg-green-600 text-white p-2 rounded hover:bg-green-700"
              disabled={!isCameraOpen}
            >
              Check attendance
            </button>
          </div>
        </div>

        {/* Right Column */}
        <div className="lg:col-span-1 flex flex-col gap-4 h-full">
          {/* Attendance Info */}
          <div className="bg-white p-4 rounded-lg shadow-md flex-3 overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Attendance information</h3>
            <div className="flex flex-col items-center">
              <img
                src={attendanceInfo.imageUrl}
                alt="Student"
                className="w-40 h-40 object-cover rounded-full mb-4 border-2 border-gray-300"
              />
              <p className="text-gray-600 my-4 text-left w-full">Student ID: {attendanceInfo.studentId}</p>
              <p className="text-gray-600 my-4 text-left w-full">Full name: {attendanceInfo.fullName}</p>
              <p className="text-gray-600 my-4 text-left w-full">Attendance time: {attendanceInfo.attendanceTime}</p>
            </div>
          </div>

          {/* Lecture Info */}
          <div className="bg-white p-4 rounded-lg shadow-md flex-0.5 overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Lecture information</h3>
            <p className="text-gray-600 my-4">Subject: {lectureInfo.subject}</p>
            <p className="text-gray-600 my-4">Lecture: {lectureInfo.lecture}</p>
            <p className="text-gray-600 my-4">Time: {lectureInfo.time}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceCheck;
