import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './HomepageCard.css';

const Home = () => {
  const sections = [
    { id: 'student', title: 'Student', path: '/student' },
    { id: 'subject', title: 'Subject', path: '/subject' },
    { id: 'lecture', title: 'Lecture', path: '/lecture' },
    { id: 'attendance', title: 'Attendance check', path: '/attendance' },
    { id: 'management', title: 'Attendance management', path: '/management' },
  ];
  const [activeCardId, setActiveCardId] = useState(null);
  const dropdownRef = useRef(null);

  const toggleDropdown = (id) => {
    setActiveCardId(activeCardId === id ? null : id);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setActiveCardId(null);
      }
    };
    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Trang chủ</h2>
      <div className="cardContainer">
        {sections.map((section) => (
          <div key={section.id} className="subjectCard">
            <Link
              to={section.path}
              style={{ textDecoration: 'none' }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="cardTheme">
                <p className="title">{section.title}</p>
              </div>
            </Link>
            <img
              src="src/assets/more.png"
              alt="Options"
              className="moreCardImageButton"
              onClick={(e) => {
                e.stopPropagation();
                toggleDropdown(section.id);
              }}
            />
            {activeCardId === section.id && (
              <div ref={dropdownRef} className="dropdownMenu">
                <button className="Edit">Xem chi tiết</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
