import React, { useState } from 'react';

const Setting = () => {
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');
  const [language, setLanguage] = useState(localStorage.getItem('language') || 'vi');
  const [fontSize, setFontSize] = useState(localStorage.getItem('fontSize') || 'medium');
  const [highContrast, setHighContrast] = useState(
    localStorage.getItem('highContrast') === 'true' || false
  );
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPasswordFields, setShowPasswordFields] = useState(false);

  const handleSave = () => {
    // Lưu cài đặt vào localStorage
    localStorage.setItem('theme', theme);
    localStorage.setItem('language', language);
    localStorage.setItem('fontSize', fontSize);
    localStorage.setItem('highContrast', highContrast);

    // Kiểm tra đổi mật khẩu
    if (showPasswordFields && (oldPassword || newPassword || confirmPassword)) {
      if (newPassword !== confirmPassword) {
        alert('Mật khẩu mới và xác nhận không khớp!');
        return;
      }
      // Ở đây có thể gọi API để đổi mật khẩu
      alert('Mật khẩu đã được đổi! (Chưa tích hợp API)');
      setOldPassword('');
      setNewPassword('');
      setConfirmPassword('');
      setShowPasswordFields(false); // Ẩn trường sau khi lưu
    }

    alert('Cài đặt đã được lưu!');
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Cài đặt</h2>
      <div className="bg-white p-6 rounded-lg shadow-md max-w-2xl">
        {/* Giao diện */}
        <div className="mb-6">
          <label className="block text-gray-700 font-semibold mb-2">Giao diện</label>
          <select
            value={theme}
            onChange={(e) => setTheme(e.target.value)}
            className="w-full p-2 border rounded"
          >
            <option value="light">Sáng</option>
            <option value="dark">Tối</option>
          </select>
        </div>

        {/* Ngôn ngữ */}
        <div className="mb-6">
          <label className="block text-gray-700 font-semibold mb-2">Ngôn ngữ</label>
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="w-full p-2 border rounded"
          >
            <option value="vi">Tiếng Việt</option>
            <option value="en">English</option>
          </select>
        </div>

        {/* Đổi mật khẩu */}
        <div className="mb-6">
          <h3
            className="text-lg font-semibold mb-2 text-gray-700 cursor-pointer hover:text-blue-600"
            onClick={() => setShowPasswordFields(!showPasswordFields)}
          >
            Đổi mật khẩu {showPasswordFields ? '▲' : '▼'}
          </h3>
          {showPasswordFields && (
            <div className="mt-4">
              <div className="mb-4">
                <label className="block text-gray-700 font-semibold mb-2">Mật khẩu cũ</label>
                <input
                  type="password"
                  value={oldPassword}
                  onChange={(e) => setOldPassword(e.target.value)}
                  className="w-full p-2 border rounded"
                  placeholder="Nhập mật khẩu cũ"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 font-semibold mb-2">Mật khẩu mới</label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className="w-full p-2 border rounded"
                  placeholder="Nhập mật khẩu mới"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 font-semibold mb-2">Xác nhận mật khẩu</label>
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  className="w-full p-2 border rounded"
                  placeholder="Xác nhận mật khẩu mới"
                />
              </div>
            </div>
          )}
        </div>

        {/* Hiển thị */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-2 text-gray-700">Hiển thị</h3>
          <div className="mb-4">
            <label className="block text-gray-700 font-semibold mb-2">Kích thước chữ</label>
            <select
              value={fontSize}
              onChange={(e) => setFontSize(e.target.value)}
              className="w-full p-2 border rounded"
            >
              <option value="small">Nhỏ</option>
              <option value="medium">Trung bình</option>
              <option value="large">Lớn</option>
            </select>
          </div>
          <label className="block text-gray-700 font-semibold mb-2">
            <input
              type="checkbox"
              checked={highContrast}
              onChange={(e) => setHighContrast(e.target.checked)}
              className="mr-2"
            />
            Chế độ tương phản cao
          </label>
        </div>

        {/* Nút lưu */}
        <button
          onClick={handleSave}
          className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Lưu cài đặt
        </button>
      </div>
    </div>
  );
};

export default Setting;
