import React, { useState } from 'react';

const AttendanceManagement = () => {
  const [attendances] = useState([
    { id: '22BI13029', name: 'Nguyễn Thị Hà Anh', subject: 'Algorithms and Data Structures', time: '10/05/2025 08:00' },
    { id: '22BI13052', name: 'Nguyễn Gia Bách', subject: 'Digital Signal Processing', time: '10/05/2025 10:00' },
  ]);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Attendance management</h2>
      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border p-2">Student ID</th>
            <th className="border p-2">Full name</th>
            <th className="border p-2">Major</th>
            <th className="border p-2">Time</th>
          </tr>
        </thead>
        <tbody>
          {attendances.map((attendance, index) => (
            <tr key={index}>
              <td className="border p-2">{attendance.id}</td>
              <td className="border p-2">{attendance.name}</td>
              <td className="border p-2">{attendance.subject}</td>
              <td className="border p-2">{attendance.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceManagement;
