import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import Login from './Login';
import Home from './Home';
import Student from './Student';
import Subject from './Subject';
import Lecture from './Lecture';
import AttendanceCheck from './AttendanceCheck';
import AttendanceManagement from './AttendanceManagement';
import Clock from './Clock';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('user'));

  if (!isLoggedIn) {
    return <Login onLogin={() => setIsLoggedIn(true)} />;
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="bg-blue-600 text-white p-4 flex justify-between items-center">
        <h1 className="text-xl font-bold">ATTENDANCE SYSTEM</h1>
        <Clock />
      </nav>
      <div className="flex">
        <div className="w-64 bg-white shadow-md">
          <ul className="p-4">
            <li className="p-2">
              <Link to="/" className="block hover:bg-blue-100">
                Home
              </Link>
            </li>
            <li className="p-2">
              <Link to="/student" className="block hover:bg-blue-100">
                Student
              </Link>
            </li>
            <li className="p-2">
              <Link to="/subject" className="block hover:bg-blue-100">
                Subject
              </Link>
            </li>
            <li className="p-2">
              <Link to="/lecture" className="block hover:bg-blue-100">
                Lecture
              </Link>
            </li>
            <li className="p-2">
              <Link to="/attendance" className="block hover:bg-blue-100">
                Attendance Check
              </Link>
            </li>
            <li className="p-2">
              <Link to="/management" className="block hover:bg-blue-100">
                Attendance Management
              </Link>
            </li>
            <li className="p-2">
              <button
                onClick={() => {
                  localStorage.removeItem('user');
                  setIsLoggedIn(false);
                }}
                className="block w-full text-left hover:bg-blue-100"
              >
                Log out
              </button>
            </li>
          </ul>
        </div>
        <div className="flex-1 p-6">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/student" element={<Student />} />
            <Route path="/subject" element={<Subject />} />
            <Route path="/lecture" element={<Lecture />} />
            <Route path="/attendance" element={<AttendanceCheck />} />
            <Route path="/management" element={<AttendanceManagement />} />
          </Routes>
        </div>
      </div>
    </div>
  );
};

export default App;
