import React, { useState, useEffect, useRef } from 'react';

const AttendanceCheck = () => {
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [attendanceInfo, setAttendanceInfo] = useState({
    studentId: '',
    fullName: '',
    attendanceTime: '',
    imageUrl: 'https://via.placeholder.com/150',
  });

  const lectureSchedule = [
    { subject: 'Object-Oriented Programming', lecture: 'Lecture 1', time: '08:00 - 09:30' },
    { subject: 'Algorithms and Data Structures', lecture: 'Lecture 2', time: '10:00 - 11:30' },
    { subject: 'Digital Signal Processing', lecture: 'Lecture 3', time: '13:00 - 14:30' },
  ];

  const [lectureInfo, setLectureInfo] = useState({
    subject: '',
    lecture: '',
    time: '',
  });

  useEffect(() => {
    const selectedLecture = lectureSchedule.find(item => item.subject === selectedSubject);
    if (selectedLecture) {
      setLectureInfo(selectedLecture);
    } else {
      setLectureInfo({ subject: '', lecture: '', time: '' });
    }
  }, [selectedSubject]);

  // Mở webcam bằng getUserMedia
  const openCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      }
      setIsCameraOpen(true);
    } catch (err) {
      console.error('Không thể mở webcam:', err);
      alert('Trình duyệt không cho phép truy cập webcam.');
    }
  };

  // Tắt webcam
  const closeCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setIsCameraOpen(false);
  };

  // Điểm danh
  const handleCheckAttendance = () => {
    setAttendanceInfo(prev => ({
      ...prev,
      studentId: '22BI13029',
      fullName: 'Nguyễn Thị Hà Anh',
      attendanceTime: new Date().toLocaleTimeString(),
    }));
  };

  return (
    <div className="p-4 h-full overflow-hidden">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Điểm danh sinh viên</h2>
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 h-[calc(100%-2rem)]">
        {/* Webcam */}
        <div className="lg:col-span-2 bg-gray-100 rounded-lg p-4 flex flex-col h-full">
          <div className="relative mb-4">
            <button
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="w-full p-2 border rounded bg-white text-left"
            >
              {selectedSubject || 'Chọn môn học'}
            </button>
            {isDropdownOpen && (
              <div className="absolute w-full mt-1 bg-white border rounded shadow-md z-10">
                {lectureSchedule.map((item, idx) => (
                  <button
                    key={idx}
                    onClick={() => {
                      setSelectedSubject(item.subject);
                      setIsDropdownOpen(false);
                    }}
                    className="w-full text-left p-2 hover:bg-gray-100"
                  >
                    {item.subject}
                  </button>
                ))}
              </div>
            )}
          </div>

          <div className="flex-1 flex items-center justify-center bg-black rounded mb-4">
            {isCameraOpen ? (
              <video
                ref={videoRef}
                className="w-full h-full object-contain rounded"
                autoPlay
                muted
              />
            ) : (
              <div className="text-gray-300">Camera chưa được mở</div>
            )}
          </div>

          <div className="flex gap-2 justify-center">
            {!isCameraOpen ? (
              <button
                onClick={openCamera}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Mở webcam
              </button>
            ) : (
              <button
                onClick={closeCamera}
                className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Tắt webcam
              </button>
            )}
            <button
              onClick={handleCheckAttendance}
              className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Điểm danh
            </button>
          </div>
        </div>

        {/* Cột bên phải */}
        <div className="lg:col-span-1 flex flex-col gap-4 h-full">
          <div className="bg-white p-4 rounded-lg shadow-md overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Thông tin điểm danh</h3>
            <div className="flex flex-col items-center">
              <img
                src={attendanceInfo.imageUrl}
                alt="Student"
                className="w-32 h-32 object-cover rounded-full mb-4 border"
              />
              <p className="text-gray-600 w-full">Mã sinh viên: {attendanceInfo.studentId}</p>
              <p className="text-gray-600 w-full">Họ tên: {attendanceInfo.fullName}</p>
              <p className="text-gray-600 w-full">Thời gian điểm danh: {attendanceInfo.attendanceTime}</p>
            </div>
          </div>

          <div className="bg-white p-4 rounded-lg shadow-md overflow-auto">
            <h3 className="text-lg font-semibold mb-2 text-gray-700">Thông tin tiết học</h3>
            <p className="text-gray-600">Môn học: {lectureInfo.subject}</p>
            <p className="text-gray-600">Tiết học: {lectureInfo.lecture}</p>
            <p className="text-gray-600">Thời gian: {lectureInfo.time}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceCheck;
