import React, { useState, useEffect, useRef } from 'react';
import axios from '../api/axios';
import * as faceapi from 'face-api.js';

const AttendanceCheck = () => {
  const videoRef = useRef(null);
  const [hasCamera, setHasCamera] = useState(false);
  const [attendanceStatus, setAttendanceStatus] = useState('');
  const [lectureSchedule] = useState({ id: 'LEC001', date: '2025-05-20' }); // Giả định
  const [error, setError] = useState('');

  // Load models cho nhận diện khuôn mặt
  useEffect(() => {
    const loadModels = async () => {
      try {
        await faceapi.nets.tinyFaceDetector.loadFromUri('/models');
        await faceapi.nets.faceLandmark68Net.loadFromUri('/models');
        await faceapi.nets.faceRecognitionNet.loadFromUri('/models');
        console.log('Face detection models loaded');
      } catch (err) {
        console.error('Error loading face models:', err);
      }
    };
    loadModels();
  }, []);

  // Khởi động webcam và nhận diện khuôn mặt
  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          setHasCamera(true);
        }
      } catch (err) {
        console.error('Error accessing webcam:', err);
        setError('Cannot access webcam. Please check permissions.');
      }
    };

    startCamera();

    const videoElement = videoRef.current;
    return () => {
      if (videoElement && videoElement.srcObject) {
        const stream = videoElement.srcObject;
        const tracks = stream.getTracks();
        tracks.forEach(track => track.stop());
      }
    };
  }, []);

  // Nhận diện khuôn mặt và đánh dấu điểm danh
  useEffect(() => {
    const detectAndMarkAttendance = async () => {
      if (videoRef.current && hasCamera) {
        const detections = await faceapi.detectAllFaces(
          videoRef.current,
          new faceapi.TinyFaceDetectorOptions()
        ).withFaceLandmarks().withFaceDescriptors();

        if (detections.length > 0) {
          // Giả định nhận diện thành công (thay bằng logic thực tế)
          const mockStudentId = '123'; // Thay bằng logic so sánh với database
          markAttendance(mockStudentId);
        }
      }
    };

    const interval = setInterval(detectAndMarkAttendance, 2000); // Kiểm tra mỗi 2 giây
    return () => clearInterval(interval);
  }, [hasCamera, lectureSchedule]);

  const markAttendance = async (studentId) => {
    try {
      const response = await axios.post('/attendance', {
        studentId,
        lectureId: lectureSchedule.id,
        date: lectureSchedule.date,
      });
      if (response.status >= 200 && response.status < 300) {
        setAttendanceStatus(`Attendance marked for student ${studentId}!`);
        setError('');
      }
    } catch (err) {
      setError('Failed to mark attendance. Please try again.');
      console.error('Attendance error:', err);
    }
  };

  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const stream = videoRef.current.srcObject;
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
      setHasCamera(false);
      setAttendanceStatus('');
      setError('');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Attendance Check</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {attendanceStatus && <p className="text-green-500 mb-4">{attendanceStatus}</p>}
      {hasCamera ? (
        <div>
          <video
            ref={videoRef}
            autoPlay
            playsInline
            className="w-full max-w-lg border rounded mb-4"
          />
          <button
            onClick={stopCamera}
            className="bg-red-500 text-white p-2 rounded hover:bg-red-600"
          >
            Stop Camera
          </button>
        </div>
      ) : (
        <p className="text-red-500">Loading webcam... or permission denied.</p>
      )}
    </div>
  );
};

export default AttendanceCheck;
