import React, { useState } from 'react';

const Setting = () => {
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');
  const [language, setLanguage] = useState(localStorage.getItem('language') || 'vi');
  const [fontSize, setFontSize] = useState(localStorage.getItem('fontSize') || 'medium');
  const [emailNotifications, setEmailNotifications] = useState(
    localStorage.getItem('emailNotifications') === 'true' || true
  );
  const [browserNotifications, setBrowserNotifications] = useState(
    localStorage.getItem('browserNotifications') === 'true' || false
  );
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showPasswordFields, setShowPasswordFields] = useState(false);
  const [showDisplayFields, setShowDisplayFields] = useState(false);
  const [showNotificationFields, setShowNotificationFields] = useState(false);

  const handleSave = () => {
    // Lưu cài đặt vào localStorage
    localStorage.setItem('theme', theme);
    localStorage.setItem('language', language);
    localStorage.setItem('fontSize', fontSize);
    localStorage.setItem('emailNotifications', emailNotifications);
    localStorage.setItem('browserNotifications', browserNotifications);

    // Kiểm tra đổi mật khẩu
    if (showPasswordFields && (oldPassword || newPassword || confirmPassword)) {
      if (newPassword !== confirmPassword) {
        alert('Mật khẩu mới và xác nhận không khớp!');
        return;
      }
      // Ở đây có thể gọi API để đổi mật khẩu
      alert('Mật khẩu đã được đổi! (Chưa tích hợp API)');
      setOldPassword('');
      setNewPassword('');
      setConfirmPassword('');
      setShowPasswordFields(false); // Ẩn trường sau khi lưu
    }

    alert('Settings has been saved!');
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Settings</h2>
      <div className="bg-white p-6 rounded-lg shadow-md max-w-2xl">
        {/* Giao diện */}
        <div className="mb-6">
          <label className="block text-lg font-semibold text-gray-700 mb-2">Theme</label>
          <select
            value={theme}
            onChange={(e) => setTheme(e.target.value)}
            className="w-full p-2 border rounded text-base"
          >
            <option value="default">Default</option>
            <option value="light">Light</option>
            <option value="dark">Dark</option>
          </select>
        </div>

        {/* Ngôn ngữ */}
        <div className="mb-6">
          <label className="block text-lg font-semibold text-gray-700 mb-2">Language</label>
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="w-full p-2 border rounded text-base"
          >
            <option value="vi">Tiếng Việt</option>
            <option value="en">English</option>
            <option value="fr">French</option>
          </select>
        </div>

        {/* Đổi mật khẩu */}
        <div className="mb-6">
          <h3
            className="text-lg font-semibold text-gray-700 cursor-pointer hover:text-blue-600 mb-2"
            onClick={() => setShowPasswordFields(!showPasswordFields)}
          >
            Change password {showPasswordFields ? '▲' : '▼'}
          </h3>
          {showPasswordFields && (
            <div className="mt-4">
              <div className="mb-4">
                <label className="block text-base font-semibold text-gray-700 mb-2">
                  Current password
                </label>
                <input
                  type="password"
                  value={oldPassword}
                  onChange={(e) => setOldPassword(e.target.value)}
                  className="w-full p-2 border rounded text-base"
                  placeholder="Enter your current password"
                />
              </div>
              <div className="mb-4">
                <label className="block text-base font-semibold text-gray-700 mb-2">
                  New password
                </label>
                <input
                  type="password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className="w-full p-2 border rounded text-base"
                  placeholder="Enter your new password"
                />
              </div>
              <div className="mb-4">
                <label className="block text-base font-semibold text-gray-700 mb-2">
                  Confirm password
                </label>
                <input
                  type="password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  className="w-full p-2 border rounded text-base"
                  placeholder="Confirm password"
                />
              </div>
            </div>
          )}
        </div>

        {/* Hiển thị */}
        <div className="mb-6">
          <h3
            className="text-lg font-semibold text-gray-700 cursor-pointer hover:text-blue-600 mb-2"
            onClick={() => setShowDisplayFields(!showDisplayFields)}
          >
            Hiển thị {showDisplayFields ? '▲' : '▼'}
          </h3>
          {showDisplayFields && (
            <div className="mt-4">
              <label className="block text-base font-semibold text-gray-700 mb-2">
                Kích thước chữ
              </label>
              <select
                value={fontSize}
                onChange={(e) => setFontSize(e.target.value)}
                className="w-full p-2 border rounded text-base"
              >
                <option value="small">Nhỏ</option>
                <option value="medium">Trung bình</option>
                <option value="large">Lớn</option>
              </select>
            </div>
          )}
        </div>

        {/* Thông báo */}
        <div className="mb-6">
          <h3
            className="text-lg font-semibold text-gray-700 cursor-pointer hover:text-blue-600 mb-2"
            onClick={() => setShowNotificationFields(!showNotificationFields)}
          >
            Thông báo {showNotificationFields ? '▲' : '▼'}
          </h3>
          {showNotificationFields && (
            <div className="mt-4">
              <label className="block text-base font-semibold text-gray-700 mb-2">
                <input
                  type="checkbox"
                  checked={emailNotifications}
                  onChange={(e) => setEmailNotifications(e.target.checked)}
                  className="mr-2"
                />
                Nhận thông báo qua email
              </label>
              <label className="block text-base font-semibold text-gray-700 mb-2">
                <input
                  type="checkbox"
                  checked={browserNotifications}
                  onChange={(e) => setBrowserNotifications(e.target.checked)}
                  className="mr-2"
                />
                Nhận thông báo trên trình duyệt
              </label>
            </div>
          )}
        </div>

        {/* Nút lưu */}
        <button
          onClick={handleSave}
          className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 text-base"
        >
          Lưu cài đặt
        </button>
      </div>
    </div>
  );
};

export default Setting;
