import React, { useState } from 'react';

const User = () => {
  const userData = localStorage.getItem('user')
    ? JSON.parse(localStorage.getItem('user'))
    : { username: 'User', email: '', firstName: '', lastName: '', dateOfBirth: '' };
  const [username, setUsername] = useState(userData.username);
  const [email, setEmail] = useState(userData.email);
  const [firstName, setFirstName] = useState(userData.firstName || '');
  const [lastName, setLastName] = useState(userData.lastName || '');
  const [dateOfBirth, setDateOfBirth] = useState(userData.dateOfBirth || '');

  const handleSave = () => {
    const updatedUser = { ...userData, username, email, firstName, lastName, dateOfBirth };
    localStorage.setItem('user', JSON.stringify(updatedUser));
    alert('Information has been saved!');
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">Welcome, {username}</h2>
      <div className="bg-white p-6 rounded-lg shadow-md max-w-lg">
        <div className="mb-4 text-center">
          <p className="text-lg font-semibold">{username}</p>
          <p className="text-gray-600">{email}</p>
        </div>
        <div className="grid grid-cols-2 gap-4">
          <div className="mb-4">
            <label className="block text-gray-700 font-semibold mb-2">First Name</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              className="w-full p-2 border rounded bg-gray-50"
              placeholder="Enter first name"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-semibold mb-2">Last Name</label>
            <input
              type="text"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              className="w-full p-2 border rounded bg-gray-50"
              placeholder="Enter last name"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-semibold mb-2">Date of Birth</label>
            <input
              type="date"
              value={dateOfBirth}
              onChange={(e) => setDateOfBirth(e.target.value)}
              className="w-full p-2 border rounded bg-gray-50"
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 font-semibold mb-2">Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full p-2 border rounded bg-gray-50"
              placeholder="Enter username"
            />
          </div>
        </div>
        <button
          onClick={handleSave}
          className="w-full bg-gray-300 text-gray-800 p-2 rounded hover:bg-gray-400 mt-4"
        >
          Update
        </button>
      </div>
    </div>
  );
};

export default User;
