import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const sections = [
    { id: 'student', title: 'Student', path: '/student' },
    { id: 'subject', title: 'Subject', path: '/subject' },
    { id: 'lecture', title: 'Lecture', path: '/lecture' },
    { id: 'attendance', title: 'Attendance Check', path: '/attendance' },
    { id: 'management', title: 'Attendance Management', path: '/management' },
  ];

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Home</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {sections.map((section) => (
          <Link
            key={section.id}
            to={section.path}
            className="block"
          >
            <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 flex items-center justify-center h-32">
              <h3 className="text-lg font-semibold text-gray-700">{section.title}</h3>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
