import React from 'react';
import { Link } from 'react-router-dom';
import './SubjectCard.css'; // Sử dụng CSS từ SubjectCard để giữ giao diện nhất quán

const HomepageCard = () => {
  const sections = [
    { id: 'student', title: 'Quản lý sinh viên', path: '/student' },
    { id: 'subject', title: 'Quản lý môn học', path: '/subject' },
    { id: 'lecture', title: 'Quản lý lịch học', path: '/lecture' },
    { id: 'attendance', title: 'Điểm danh', path: '/attendance' },
    { id: 'management', title: 'Quản lý điểm danh', path: '/management' },
  ];

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Trang chủ</h2>
      <div className="cardContainer">
        {sections.map((section) => (
          <Link
            key={section.id}
            to={section.path}
            style={{ textDecoration: 'none' }}
          >
            <div className="subjectCard">
              <div className="cardTheme">
                <p className="title">{section.title}</p>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default HomepageCard;
